import React from 'react';
import { Link } from 'react-router-dom';

import '../../App.css';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Paper from '@material-ui/core/Paper';
import { useFetch } from '../../hooks/useFetch';
import UserTable from '../userTable/UserTable';

export const Home = () => {
	const { data, loading, error, handleCancelRequest } = useFetch(
		'http://localhost:9090/users',
		'GET'
	);

	const handleCancel = () => {
		handleCancelRequest();
	};
	return (
		<div>
			<AppBar position="static" color="default">
				<Toolbar>
					<Typography variant="h6" color="inherit">
						Test React
					</Typography>
				</Toolbar>
			</AppBar>
			<div
				style={{
					width: '80%',
					margin: '0 auto',
					marginTop: 50,
				}}
			>
				<Paper>
					<div
						style={{
							minHeight: 200,
							padding: 25,
							minWidth: 1800,
						}}
					>
						<Typography
							variant="title"
							color="inherit"
							style={{ display: 'inline' }}
						>
							Listado de usuarios
						</Typography>
						<Button
							variant="fab"
							color="primary"
							aria-label="Add"
							style={{ width: 35, height: 30 }}
						>
							<AddIcon />
						</Button>
						<Link to="/add" style={{ textDecoration: 'none' }}>
							<button
								style={{
									marginLeft: 10,
									padding: '8px 16px',
									background: 'aqua',
									color: 'black',
									border: 'none',
									borderRadius: 14,
									cursor: 'pointer',
								}}
							>
								Agregar Usuario
							</button>
						</Link>
						<button onClick={handleCancel}>Cancel Request</button>
						{loading && <p>Loading...</p>}
						{error && <p>{error}</p>}
						{data && (
							<UserTable
								users={data} // Pasar los datos de usuarios a la tabla
								// onEdit={handleEditUser} // Pasar la función de editar usuario
								// onDelete={handleDeleteUser} // Pasar la función de eliminar usuario
							/>
						)}
					</div>
				</Paper>
			</div>
		</div>
	);
};
