import React, { useState } from 'react';
import {
	Dialog,
	DialogTitle,
	DialogContent,
	DialogActions,
	Button,
	TextField,
	Snackbar,
} from '@material-ui/core';

export const AddUser = ({
	open,
	onClose,
	onSubmit,
	onSubmitSuccess,
	onSubmitError,
}) => {
	const [userData, setUserData] = useState({
		name: '',
		lastName: '',
		age: '',
		email: '',
	});
	const [successMessage, setSuccessMessage] = useState('');
	const [errorMessage, setErrorMessage] = useState('');
	const [errors, setErrors] = useState({
		name: false,
		lastName: false,
		email: false,
	});

	const handleChange = (event) => {
		const { name, value } = event.target;
		setUserData({
			...userData,
			[name]: value,
		});
		// Reset errors when user starts typing
		setErrors({
			...errors,
			[name]: false,
		});
	};

	const handleSubmit = async () => {
		// Validate form
		if (!userData.name || !userData.lastName || !userData.email) {
			setErrors({
				name: !userData.name,
				lastName: !userData.lastName,
				email: !userData.email,
			});
			return;
		}

		// Email validation
		const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
		if (!emailPattern.test(userData.email)) {
			setErrors({
				...errors,
				email: true,
			});
			return;
		}

		try {
			await onSubmit(userData);
			setSuccessMessage('Usuario agregado con éxito');
			setErrorMessage('');
			setUserData({
				name: '',
				lastName: '',
				age: '',
				email: '',
			});
			onSubmitSuccess(); // Llama a la función onSubmitSuccess de Home
		} catch (error) {
			setSuccessMessage('');
			setErrorMessage('Error al agregar usuario: ' + error.message);
			onSubmitError(); // Llama a la función onSubmitError de Home
		}
	};

	const handleSnackbarClose = () => {
		setSuccessMessage('');
		setErrorMessage('');
	};

	return (
		<>
			<Dialog
				open={open}
				onClose={onClose}
				aria-labelledby="form-dialog-title"
			>
				<DialogTitle id="form-dialog-title">
					Agregar Usuario
				</DialogTitle>
				<DialogContent>
					<TextField
						autoFocus
						margin="dense"
						label="Nombre"
						name="name"
						value={userData.name}
						onChange={handleChange}
						error={errors.name}
						helperText={
							errors.name ? 'El nombre es obligatorio' : ''
						}
						required
						fullWidth
					/>
					<TextField
						margin="dense"
						label="Apellido"
						name="lastName"
						value={userData.lastName}
						onChange={handleChange}
						error={errors.lastName}
						helperText={
							errors.lastName ? 'El apellido es obligatorio' : ''
						}
						required
						fullWidth
					/>
					<TextField
						margin="dense"
						label="Edad"
						name="age"
						type="number"
						value={userData.age}
						onChange={handleChange}
						fullWidth
					/>
					<TextField
						margin="dense"
						label="Email"
						name="email"
						type="email"
						value={userData.email}
						onChange={handleChange}
						error={errors.email}
						helperText={
							errors.email
								? 'Por favor, introduce un correo electrónico válido'
								: ''
						}
						required
						fullWidth
					/>
				</DialogContent>
				<DialogActions>
					<Button onClick={onClose} color="primary">
						Cancelar
					</Button>
					<Button onClick={handleSubmit} color="primary">
						Agregar
					</Button>
				</DialogActions>
			</Dialog>
			<Snackbar
				open={!!successMessage || !!errorMessage}
				autoHideDuration={6000}
				onClose={handleSnackbarClose}
			>
				<div
					style={{
						backgroundColor: errorMessage ? '#f44336' : '#4caf50',
						color: '#fff',
						padding: '10px 20px',
						borderRadius: '4px',
						textAlign: 'center',
					}}
				>
					{errorMessage || successMessage}
				</div>
			</Snackbar>
		</>
	);
};
