import React from 'react';
import { useFetch } from '../../hooks/useFetch';

const UserDeletionExample = () => {
	const { loading, error, handleFetch } = useFetch(
		'http://localhost:9090/users/1',
		'DELETE'
	);

	const handleDeleteUser = () => {
		handleFetch(); // Realiza la petición DELETE al hacer clic en el botón
	};

	return (
		<div>
			<h2>Delete User with ID 1</h2>
			<button onClick={handleDeleteUser} disabled={loading}>
				Delete User
			</button>
			{loading && <p>Loading...</p>}
			{error && <p>{error.message}</p>}{' '}
			{/* Mostramos el mensaje de error */}
		</div>
	);
};

export default UserDeletionExample;
