import React from 'react';
import { useFetch } from '../../hooks/useFetch';
import UserTable from '../userTable/UserTable';
import UserDeletionExample from '../userDeletion/UserDeletionExample';

export const Home = () => {
	const { data, loading, error, handleFetch: handleFetchUsers } = useFetch(
		'http://localhost:9090/users/',
		'GET'
	);

	const {
		loading: deleteLoading,
		error: deleteError,
		handleFetch: handleDeleteUser,
	} = useFetch('http://localhost:9090/users/1', 'DELETE');

	const handleCancelRequest = () => {
		handleFetchUsers(); // Cancela la solicitud previa y refresca los datos
	};

	return (
		<div>
			<div>
				<h2>Listado de usuarios</h2>
				<button onClick={handleCancelRequest}>
					Cancelar Solicitud
				</button>
				{loading && <p>Loading...</p>}
				{error && <p>{error}</p>}
				{data && <UserTable users={data} />}
				{/* Agregamos el componente de eliminación de usuario */}
				<UserDeletionExample />
				<button onClick={handleDeleteUser} disabled={deleteLoading}>
					Delete User
				</button>{' '}
				{/* Agregamos el botón para eliminar usuario */}
				{deleteLoading && <p>Loading...</p>}
				{deleteError && <p>{deleteError.message}</p>}
			</div>
		</div>
	);
};

export default Home;
