export const validateForm = (
	userData,
	setSnackbarOpen,
	setSuccessMessage,
	emailError
) => {
	const { name, lastName, age, email } = userData;

	if (!name || !lastName || !age || !email) {
		setSnackbarOpen(true);
		setSuccessMessage('');
		return false;
	}

	if (emailError) {
		setSnackbarOpen(true);
		setSuccessMessage('');
		return false;
	}

	return true;
};
