import React, { useState } from 'react';
import { useFetch } from '../../hooks/useFetch';

const UserDeletionExample = () => {
	const [deleteStatus, setDeleteStatus] = useState(null);
	const { loading, error, handleCancelRequest } = useFetch(
		'http://localhost:9090/users/1',
		'DELETE'
	);

	const handleDeleteUser = () => {
		handleCancelRequest(); // Cancelar cualquier petición previa
	};

	return (
		<div>
			<h2>Delete User with ID 1</h2>
			<button onClick={handleDeleteUser} disabled={loading}>
				Delete User
			</button>
			{loading && <p>Loading...</p>}
			{error && <p>Error: {error.message}</p>}
			{deleteStatus && <p>{deleteStatus}</p>}
		</div>
	);
};

export default UserDeletionExample;
